import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const LoginForm = () => {
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const location = useLocation();
  const successMessageRef = useRef('');

  useEffect(() => {
    const successMessage = location.state && location.state.successMessage;

    if (successMessage != null && successMessage !== "" && successMessage !== successMessageRef.current) {
      // Mostrar el mensaje de éxito
      setSuccess(successMessage);
      successMessageRef.current = successMessage;

      // Limpiar el mensaje después de 5 segundos
      const timeoutId = setTimeout(() => {
        setSuccess(null);
      }, 5000);

      // Devolver una función de limpieza para cancelar el temporizador si el componente se desmonta
      return () => {
        clearTimeout(timeoutId);
      };
    }
  }, [location.state]);

  return (
    <div className="container">
      <div className="form-container form-login">
        <h2>SOC - Centro de Operaciones de Seguridad</h2>
        <form>
          <div className="mb-3">
            <label htmlFor="userId" className="form-label">ID de Persona:</label>
            <input type="text" className="form-control" id="userId" name="userId" maxLength="10" required />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Contraseña:</label>
            <input type="password" className="form-control" id="password" name="password" maxLength="255" minLength="8" required />
          </div>
          <div className="mb-3 d-grid gap-2">
            <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
            <Link to="/registro" className="btn btn-secondary">Registrarse</Link>
          </div>
        </form>
      </div>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}
    </div>
  );
};

export default LoginForm;
