import React, { useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { fetchPostTareas } from '../controllers/TareasController';
import LoadingOverlay from './LoadingOverlay';

const TareasForm = () => {
    let { id } = useParams();

    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate  = useNavigate();

    const [formData, setFormData] = useState({
        titulo: '',
        descripcion: ''
      });

      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
          }));
      };

    const handleSubmit = async (e) => {
        /*e.preventDefault();
        setLoading(true);
        const userId = localStorage.getItem('userId');
        const data = await fetchPostAmenaza(userId, formData);
        if (data.success){
            setError(null);
            navigate('/listado-amenazas', { state: { successMessage: 'Amenaza reportada' } });
        }
        else{
            setError(data.error);
        }

        setLoading(false);*/
      };

  return (
    <div className="container">
      {loading && <LoadingOverlay />}
      <div className="form-container form-register">
        <h2>{ parseInt(id) === 0 ? "Crear" : "Editar" } tarea</h2>
        <form onSubmit={handleSubmit}>
            <div className="row">
            <div className="mb-3 col-12">
            <label htmlFor="nombre" className="form-label">Titulo:</label>
            <input type="text" className="form-control" id="titulo" name="titulo" maxLength="20" onChange={handleChange} value={formData.titulo} required />
          </div>
          <div className="mb-3 col-12">
            <label htmlFor="descripcion" className="form-label">Descripción:</label>
            <textarea
                className="form-control"
                id="descripcion"
                name="descripcion"
                onChange={handleChange}
                value={formData.descripcion}
                required
            ></textarea>
            </div>
            <div className="mb-3 col-6">
      <label htmlFor="fechaInicio" className="form-label">Fecha de inicio:</label>
      <input type="date" className="form-control" id="fechaInicio" name="fechaInicio" onChange={handleChange} value={formData.fechaInicio} required />
    </div>
    <div className="mb-3 col-6">
      <label htmlFor="fechaFin" className="form-label">Fecha de finalización:</label>
      <input type="date" className="form-control" id="fechaFin" name="fechaFin" onChange={handleChange} value={formData.fechaFin} required />
    </div>
    <div className="mb-3 col-6">
      <label htmlFor="tiempo" className="form-label">Tiempo:</label>
      <input type="time" className="form-control" id="tiempo" name="tiempo" onChange={handleChange} value={formData.tiempo} required />
    </div>
    <div className="mb-3 col-6">
      <label htmlFor="prioridad" className="form-label">Prioridad:</label>
      <select className="form-select" id="prioridad" name="prioridad" onChange={handleChange} value={formData.prioridad} required>
        <option value="">Seleccionar</option>
        <option value="baja">Baja</option>
        <option value="media">Media</option>
        <option value="alta">Alta</option>
      </select>
    </div>
          <div className="mb-3 d-grid gap-2 d-flex justify-content-center">
            <button type="submit" className="btn btn-primary">Reportar</button>
            <Link to="/" className="btn btn-secondary">Volver</Link>
          </div>
            </div>
        </form>
      </div>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default TareasForm;
