import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { fetchAreas } from '../controllers/UsuarioController';
import LoadingOverlay from './LoadingOverlay';

const RegistrationForm = () => {
    const [areas, setAreas] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [selectedArea, setSelectedArea] = useState("");

    const [formData, setFormData] = useState({
        id: '',
        identificacion: null,
        nombre: '',
        correo: '',
        area: 0,
        clave: '',
        administrador: false,
      });

      const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prevData) => ({
          ...prevData,
          [name]: type === 'checkbox' ? checked : value,
        }));
      };
  
    const fetchAreasFromApi = async () => {
        const data = await fetchAreas();
        if (data.success){
            setError(null);
            setAreas(data.respuesta);
        }
        else{
            console.log(data.error);
            setError(data.error);
        }

        setLoading(false);
    };
  
    useEffect(() => {
      fetchAreasFromApi();
    }, []);

  return (
    <div className="container">
        {loading && <LoadingOverlay />}
      <div className="form-container form-register">
        <h2>Registro</h2>
        <form>
            <div className="row">
            <div className="mb-3 col-12 col-md-6">
            <label htmlFor="idPersona" className="form-label">ID de Persona:</label>
            <input type="text" className="form-control" id="idPersona" name="idPersona" maxLength="10" required />
          </div>
          <div className="mb-3 col-12 col-md-6">
            <label htmlFor="identificacion" className="form-label">Identificación:</label>
            <input type="number" className="form-control" id="identificacion" name="identificacion" required />
          </div>
          <div className="mb-3 col-12 col-md-6">
            <label htmlFor="nombre" className="form-label">Nombre:</label>
            <input type="text" className="form-control" id="nombre" name="nombre" maxLength="50" required />
          </div>
          <div className="mb-3 col-12 col-md-6">
            <label htmlFor="correo" className="form-label">Correo:</label>
            <input type="email" className="form-control" id="correo" name="correo" maxLength="255" />
          </div>
          <div className="mb-3 col-12 col-md-6">
            <label htmlFor="area" className="form-label">Área:</label>
            <select className="form-select" id="area" name="area" required value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)}>
                <option value="" disabled>Seleccione un área</option>
                {areas && areas.length > 0 ? (areas.map(area => (
                    <option key={area.codigo} value={area.codigo}>{area.descripcion}</option>
                ))) : ("")}
            </select>
          </div>
          <div className="mb-3 col-12 col-md-6">
            <label htmlFor="contraseña" className="form-label">Contraseña:</label>
            <input type="password" className="form-control" id="contraseña" name="contraseña" maxLength="255" minLength="8" required />
          </div>
          <div className="mb-2 mt-2 col-12 d-flex justify-content-center">
            <input type="checkbox" className="form-check-input" id="esAdmin" name="esAdmin" />
            <label className="form-check-label" htmlFor="esAdmin">¿Administrador?</label>
          </div>
          <div className="mb-3 d-grid gap-2 d-flex justify-content-center">
            <button type="submit" className="btn btn-primary">Registrarse</button>
            <Link to="/" className="btn btn-secondary">Volver</Link>
          </div>
            </div>
        </form>
      </div>
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default RegistrationForm;
