import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import RegistrationForm from './RegistrationForm';

const LoginForm = () => {
  const [showRegistration, setShowRegistration] = useState(false);

  const handleShowRegistration = () => {
    setShowRegistration(true);
  };

  const handleBackToLogin = () => {
    setShowRegistration(false);
  };

  return (
    <div className="container">
      <div className="login-form">
        {showRegistration ? (
          <RegistrationForm onBack={handleBackToLogin} />
        ) : (
          <>
            <h2>SOC - Centro de Operaciones de Seguridad</h2>
            <form>
              {/* Campos de inicio de sesión */}
              <div className="mb-3">
                <label htmlFor="userId" className="form-label">ID de Persona:</label>
                <input type="text" className="form-control" id="userId" />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Contraseña:</label>
                <input type="password" className="form-control" id="password" />
              </div>
              <div className="mb-3 d-grid gap-2">
                <button type="submit" className="btn btn-primary">Iniciar Sesión</button>
                <button type="button" className="btn btn-secondary" onClick={handleShowRegistration}>Registrarse</button>
              </div>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default LoginForm;
