const API_URL = 'http://localhost:8080';

export const fetchAreas = async () => {
  try {
    const response = await fetch(`${API_URL}/usuario/areas`, { mode: 'no-cors' });
    const data = await response;
    return data;
  } catch (error) {
    console.error('Error al obtener áreas:', error);
  }
};
