    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [loading, setLoading] = useState(false);
    const { userId } = useUser();

    const [amenazas, setAmenazas] = useState([]);

    useEffect(() => {
        fetchAreasFromApi();
      }, []);
};

