import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { fetchTareas } from '../controllers/TareasController';
import LoadingOverlay from './LoadingOverlay';

const ListadoTareas = () => {
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [loading, setLoading] = useState(false);

    const [tareas, setTareas] = useState([]);

    const fetchTareasFromApi = async () => {
        setLoading(true);
        const data = await fetchTareas();
        if (data.success){
            setError(null);
            setTareas(data.respuesta);
        }
        else{
            setError(data.error);
        }

        setLoading(false);
    };

    useEffect(() => {
      fetchTareasFromApi();
    }, []);

    return (
      <div className="container">
      {loading && <LoadingOverlay />}
        <div className="form-container form-list">
          <h2>Horario</h2>
          <div className='row'>
          <div className="mb-3 d-grid gap-2 d-flex justify-content-center">
              <Link to="/reportar" className="btn btn-primary">Cr</Link>
            </div>
          </div>
        </div>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
      </div>
    );
};

export default ListadoTareas;
