const API_URL = 'http://localhost:8080/usuario';

export const fetchAreas = async () => {
  try {
    const response = await fetch(`${API_URL}/areas`);
    const data = await response.json();

    if (data.success === false) {
        throw new Error(data.mensajeError);
    }

    return data;
  } catch (error) {
    console.error(error);
    return 
  }
};
